section.para:
Network Server Sample:
			"network" : {
		"serverPorts" : {
			"relayPort1" : {
				"ip" : "127.0.0.1",
				"port" : 21000
			}
		}, 
		"ipFilter" : {
			"enabled" : false,
			"allowedIPs" : [
				"127.0.0.1",
				"127.0.0.2"
			]
		}
	}

Network Client
			Sample:"network" : {
		"connections" : {
			"exampleConnection1" : {
				"host" : "127.0.0.1",
				"port" : 21000,
				"connectOnStartup": false,
				"autoRetryInitialDelay" : 5,
				"autoRetryMaximumDelay" : 300,
				"autoRetryDelayMultiplier" : 1.5
			}
		}
	}

Third Party integration Python path example:
			"thirdPartyIntegration": {
			"python":{
			"binaryPath": "/path/to/python/executable"
					}
			}

 SSH Server
			Sample:"sshServer" : {
		"enabled" : true,
		"ip" : "127.0.0.1",
		"port" : 31005,
		"accounts" : {
			"ra_demo" : {
				// hashed form of the "ra_demo" test password; DO NOT reuse this for live accounts!
				"passwordHash" : "$2a$10$qxCBuEvq0xWoOlox2dVbCu8zCYsyxQMBe5SAnS2HId0uaEp59aAu2",
				"role" : "remote_access_user",
				"enabled" : true
			}
		}
	}

 Uplink Connection
			Sample:"uplink" : {
		"uplinkConnections" : {
			"exampleUplinkConnectionID" : {
				"displayName" : "example",
				"clientID": "client1",
				"host" : "127.0.0.1",
				"port" : 31005,
				"connectOnStartup": false,
				"autoRetry" : false,
				"isGateway" : false,
				"loginName" : "ra_demo"
				//The passphrase is not stored here, it has to be entered when connecting.
			}
		}
	}

 Remote Access Connection Sample
			"sshRemoteAccess" : {
				"sshConnections" : {
					"exampleSSHConnection" : {
						"displayName" : "example",
						"host" : "127.0.0.1",
						"port" : 31005,
						"connectOnStartup": false,
						"autoRetry" : false,
						"loginName" : "ra_demo"
						//The passphrase is not stored here, it has to be entered when connecting.
					}
				}
			}
